<?php	// ENABLE POST THUMBNAILSif ( function_exists( 'add_theme_support' ) ) { // Added in 2.9	add_theme_support( 'post-thumbnails' );	set_post_thumbnail_size( 297, 188, true ); // Normal post thumbnails	add_image_size( 'single-post-thumbnail', 367, 217 ); // Permalink thumbnail size}	// SIDEBARSregister_sidebar(array(	'name' => 'Right Sidebar for Homepage',	'id' => 'rightsidebar',	'before_widget' => '<li>',	'after_widget' => "</li>",	'before_title' => "<h4>",	'after_title' => "</h4><hr />",));register_sidebar(array(	'name' => 'Left sidebar for Pages',	'id' => 'leftsidebar',	'before_widget' => '<li>',	'after_widget' => "</li>",	'before_title' => "<h4>",	'after_title' => "</h4>",));function content($num, $home) {    $link = get_permalink();	$ending = "...";	$theContent = get_the_content();	$output = $theContent;	/* $output = preg_replace('/<img[^>]+./','', $theContent); */	$output = strip_tags($output);	$output = preg_replace('/<h[^>]+./','', $output);	$output = preg_replace('/<a[^>]+./','', $output);	$output = preg_replace('/<img[^>]+./','', $output);	$output = preg_replace('/<object[^>]+./','', $output);	$output = preg_replace('/<param[^>]+./','', $output);	$output = preg_replace('/<embed[^>]+./','', $output);	$output = preg_replace('/\[caption[^>]+./','', $output);	$theContent = $output;	$limit = $num+1;	$content = explode(' ', $output, $limit);	array_pop($content);	$content = implode(" ",$content).$ending;	$imgBeg = strpos($theContent, '<img');	$post = substr($theContent, $imgBeg);	$imgEnd = strpos($post, '>');	$postOutput = substr($post, 0, $imgEnd+1);	$result = preg_match('/width="([0-9]*)" height="([0-9]*)"/', $postOutput, $matches);	if ($result) {	    $pagestring = $matches[0];		$postOutput = str_replace($pagestring, "", $postOutput);	}	if(stristr($postOutput,'<img src=')) { echo '<a href="'.$link.'">'.$postOutput."</a>".$content; } else {	    echo $content;	}	if ($home) {	    $readmore = '<p><a href="'.$link.'" class="learnmore" alt="Learn More"></a></p>';	} else {		$readmore = '<p class="readmore"><a href="'.$link.'">Read More</a></p>';	}	echo $readmore;}function getanalytics($theContent) {    $finalContent = '';	$num = 50;	$ending = "...";	$output = $theContent;	$output = strip_tags($output);	$output = preg_replace('/<h[^>]+./','', $output);	$output = preg_replace('/<a[^>]+./','', $output);	$output = preg_replace('/<img[^>]+./','', $output);	$output = preg_replace('/<object[^>]+./','', $output);	$output = preg_replace('/<param[^>]+./','', $output);	$output = preg_replace('/<embed[^>]+./','', $output);	$output = preg_replace('/\[caption[^>]+./','', $output);	$theContent = $output;		$limit = $num+1;	$content = explode(' ', $output, $limit);	array_pop($content);	$content = implode(" ",$content);		$temp = str_replace($content,'',$output);	$limit = $num+1;	$content2 = explode(' ', $temp, $limit);	array_pop($content2);	$content2 = implode(" ",$content2).$ending;		$finalContent = '<div class="left analytics_content">'.$content.'</div>';	$finalContent .= '<div class="right analytics_content">'.$content2.'</div>';	$finalContent .= '<div class="clearAll"></div>';		echo $finalContent;}function catch_that_image($content_analytics) {    $output = preg_match_all('/<img.+src=[\'"]([^\'"]+)[\'"].*>/i', $content_analytics, $matches);    $first_img = $matches [1] [0];    if(empty($first_img)){ //Defines a default image        $first_img = "/images/default.jpg";    }        return $first_img;}	?>